    fn read_file(path: &str) -> Result<Vec<u8>, Error> {
        let path = Path::new(path);
        println!("path = {:?}", path);
        let mut file = File::open(path)?;
        let mut buf = Vec::<u8>::new();
        file.read_to_end(&mut buf)?;
        Ok(buf)
    }

